CREATE DEFINER=`user`@`%` PROCEDURE `dataToDBSPCharts`(
IN userId VARCHAR(255) ,
IN periodType VARCHAR(10)
)
BEGIN
	DECLARE tblMonthsName NVARCHAR(255) ;
    DECLARE tM DATE ;
    DECLARE ttM DATE ;
    DECLARE cM int;
    DECLARE monthsOfYear INT DEFAULT 1;
	DECLARE years INT ;
    DECLARE mstring NVARCHAR(255) ;
    set tblMonthsName = 'temp_tbl';
	set tM= DATE_ADD(current_date(),INTERVAL -12  MONTH) ;
    set years = year(current_date())-10;
   
		CREATE TEMPORARY TABLE temp_tbl (
		id INT NOT NULL,
		period VARCHAR(45) NULL,
		deposit decimal(18,2),
		buy decimal(18,2),
		sell decimal(18,2),
		profit decimal(18,2),
		PRIMARY KEY (`id`)) 
		ENGINE=MEMORY ;
	IF periodType='M' THEN
		
		WHILE monthsOfYear <= 12 DO
        
           set ttM = date_add(tM,INTERVAL monthsOfYear MONTH);
			set cM = MONTH(ttM);
			SELECT DATE_FORMAT(CONCAT(YEAR(ttM),'-',LPAD(cM,2,'0'),'-04 22:23:00'), '%Y-%b') into mstring;
			insert into temp_tbl(id,period,deposit,buy,sell,profit)VALUES(
			monthsOfYear,mstring,
			ifnull(getDepositByMonthOrYear(userId,'M',ttM),0),
			ifnull(getBuyByMonthOrYear(userId,'M',ttM),0),
			 ifnull(getSellByMonthOrYear(userId,'M',ttM),0),
			 ifnull(getProfitByMonthOrYear(userId,'M',ttM),0)
			);
			
			SET monthsOfYear = monthsOfYear + 1;
		END WHILE;
        
	END IF;
    IF periodType='Y' THEN
		WHILE years <= year(curdate()) DO
			SELECT years into mstring;
			insert into temp_tbl(id,period,deposit,buy,sell,profit)VALUES(
			years,mstring,
			ifnull(getDepositByMonthOrYear(userId,'Y',years),0),
			ifnull(getBuyByMonthOrYear(userId,'Y',years),0),
			 ifnull(getSellByMonthOrYear(userId,'Y',years),0),
			 ifnull(getProfitByMonthOrYear(userId,'Y',years),0)
			);
			
			SET years = years + 1;
		END WHILE;
    END IF;
	select * from temp_tbl;
	drop table temp_tbl;
    
END ;;
