CREATE DEFINER=`user`@`%` PROCEDURE `generate_account_balance`(
IN userId VARCHAR(255),
IN effectivedate datetime
)
BEGIN
DECLARE effDate DATETIME;

DECLARE tbl2 NVARCHAR(50);
set tbl2 =CONCAT(userId,'_');
SELECT DATE_ADD(effectivedate, INTERVAL -1 DAY) into effDate; 
drop table if exists userId;
create TABLE  userId 
SELECT DATE_FORMAT(effDate,"%Y-%m-%d") as trans_date , 'BROUGHT FORWARD BALANCE' as trans_type , sum(in_amount) as in_amount , sum(out_amount) as out_amount,0 AS balance , company
FROM cash_book  where user_id=userId and DATE(trans_date)<DATE(effectivedate)
UNION ALL
SELECT DATE_FORMAT(trans_date,"%Y-%m-%d") as trans_date , trans_type  , in_amount , out_amount,0 AS balance , company
FROM cash_book  where user_id=userId and DATE(trans_date)>=DATE(effectivedate) order by trans_date ASC;

 

BEGIN
 DECLARE finished INTEGER DEFAULT 0;
 
 DECLARE transDate VARCHAR(10);
 DECLARE transType VARCHAR(255);
 DECLARE Cmpny VARCHAR(255);
 DECLARE inAmount DECIMAL(18,2);
 DECLARE outAmount DECIMAL(18,2);
 DECLARE BAL DECIMAL(19,2);
 DECLARE comName NVARCHAR(50);
 
 DEClARE curAccount
		CURSOR FOR 
			SELECT trans_date ,trans_type,in_amount,out_amount,company
            from userId;
            
DECLARE CONTINUE HANDLER 
        FOR NOT FOUND SET finished = 1;
OPEN curAccount;
drop table if exists tbl2;
