		IF finished = 1 THEN 
			LEAVE getAccount;
		END IF;
      
        IF inAmount > 0 THEN
			select  BAL + inAmount into BAL;
        END IF;
		IF outAmount > 0 THEN
			select  BAL - outAmount into BAL;
        END IF;
   
       SET comName = '';
