			select code into comName from  companies where id=Cmpny;
            SET transType =  CONCAT(transType ,' - ' , comName);
        END IF;
         
        insert into tbl2
		select uuid(),transDate,transType,inAmount,outAmount,FORMAT(BAL,2);
	END LOOP getAccount;
	CLOSE curAccount;
 
 select * from tbl2;
	drop table tbl2;
  drop table userId;
