CREATE DEFINER=`user`@`%` PROCEDURE `topGainers`( userId nvarchar(50))
BEGIN
SELECT 
uuid() as id,
c.name,
c.code as company,
(select sum(stl.net_amount)  from stock_ledger stl    where stl.trans_type ='BUY' and stl.company=c.id) as investment,
		sum((s.net_amount - (select (st.net_amount / st.stock_in) * s.out_stock   from stock_ledger st  
        where st.trans_type !='SALE' and st.lot_number= s.lot_number)))  as gain,
         
         case when sum((s.net_amount - (select (st.net_amount / st.stock_in) * s.out_stock   from stock_ledger st  
        where st.trans_type !='SALE' and st.lot_number= s.lot_number)))  > 0 then  true else false end as delta_up,
        
	ROUND((sum((s.net_amount - (select (st.net_amount / st.stock_in) * s.out_stock   from stock_ledger st  
        where st.trans_type !='SALE' and st.lot_number= s.lot_number))) / (select sum(stl.net_amount)  from stock_ledger stl    where stl.trans_type ='BUY' and stl.company=c.id)) * 100 ,2)AS gain_presentage
		FROM stock_ledger s 
		inner join  companies c ON s.company=c.id
        
		where s.trans_type='SALE' 
        and s.user_id=userId 
        group by s.company order by 
        sum((s.net_amount - (select (st.net_amount / st.stock_in) * s.out_stock   from stock_ledger st  
        where st.trans_type !='SALE' and st.lot_number= s.lot_number))) DESC limit 10
        ;
END ;;
